// Remove all newlines (\n) and backslashes from the content field
const inputData = $input.all()[0].json;

// Get the content field
const originalText = inputData.content;

// Handle case where content might be undefined
if (!originalText) {
  return {
    error: "No content field found",
    inputData: inputData
  };
}

// Remove all \n characters, \r characters, and backslashes
const cleanedText = originalText
  .replace(/\\n/g, ' ')    // Remove literal \n sequences  
  .replace(/\n/g, ' ')     // Remove actual newlines
  .replace(/\\r/g, ' ')    // Remove literal \r sequences
  .replace(/\r/g, ' ')     // Remove actual carriage returns
  .replace(/\\/g, '')      // Remove all remaining backslashes
  .replace(/\s+/g, ' ')    // Replace multiple spaces with single space
  .trim();                 // Remove leading/trailing spaces

// Return the cleaned text
return {
  content: cleanedText,
  originalLength: originalText.length,
  cleanedLength: cleanedText.length
};

// Get the input data - handle both parsed JSON and string responses
let response;
const inputData = $input.all()[0].json;

// Check if the data is a string that needs parsing
if (typeof inputData.data === 'string') {
  try {
    response = JSON.parse(inputData.data);
  } catch (e) {
    console.log('Failed to parse JSON string:', e);
    return [{ json: { error: 'Failed to parse response data', rawData: inputData } }];
  }
} else {
  response = inputData;
}

// Validate response structure
if (!response || !response.data || !Array.isArray(response.data)) {
  console.log('Invalid response structure:', response);
  return [{ json: { error: 'Invalid API response structure', response: response } }];
}

const documents = response.data;
const openDockets = [];

console.log(`Processing ${documents.length} documents`);

for (const doc of documents) {
  // Safety checks for document structure
  if (!doc || !doc.attributes) {
    console.log('Skipping document with no attributes:', doc?.id);
    continue;
  }

  const attrs = doc.attributes;

  // Only process documents that are explicitly open for comments
  if (attrs.openForComment !== true || attrs.withinCommentPeriod !== true) {
    console.log(`Skipping closed document: ${doc.id}`);
    continue;
  }

  console.log(`Processing open document: ${doc.id} - ${attrs.title}`);

  // Build the result object with safe property access
  const docketInfo = {
    docketId: attrs.docketId,
    documentId: doc.id,
    title: attrs.title,
    agencyId: attrs.agencyId,
    documentType: attrs.documentType,
    openForComment: attrs.openForComment,
    withinCommentPeriod: attrs.withinCommentPeriod,
    commentEndDate: attrs.commentEndDate,
    commentStartDate: attrs.commentStartDate,
    postedDate: attrs.postedDate,
    subtype: attrs.subtype,
    frDocNum: attrs.frDocNum,
    allowLateComments: attrs.allowLateComments,
    objectId: attrs.objectId,
    lastModifiedDate: attrs.lastModifiedDate,
    withdrawn: attrs.withdrawn,
    highlightedContent: attrs.highlightedContent,

    // API Links
    apiDocumentLink: doc.links?.self,
    apiDocketLink: `https://api.regulations.gov/v4/dockets/${attrs.docketId}`,

    // Public Regulations.gov Web Links (clickable in browser)
    webDocumentLink: `https://www.regulations.gov/document/${doc.id}`,
    webDocketLink: `https://www.regulations.gov/docket/${attrs.docketId}`,
    webCommentLink: `https://www.regulations.gov/comment/${doc.id}`,

    // Federal Register Link (if available)
    federalRegisterLink: attrs.frDocNum ? `https://www.federalregister.gov/documents/${new Date(attrs.postedDate).getFullYear()}/${String(new Date(attrs.postedDate).getMonth() + 1).padStart(2, '0')}/${String(new Date(attrs.postedDate).getDate()).padStart(2, '0')}/${attrs.frDocNum}` : null
  };

  openDockets.push(docketInfo);
}

// Remove duplicates by docketId - keep the most recent document per docket
const uniqueDockets = [];
const docketMap = new Map();

for (const docket of openDockets) {
  const existingDocket = docketMap.get(docket.docketId);

  if (!existingDocket) {
    docketMap.set(docket.docketId, docket);
  } else {
    // Keep the more recently posted document
    const currentDate = new Date(docket.postedDate);
    const existingDate = new Date(existingDocket.postedDate);

    if (currentDate > existingDate) {
      docketMap.set(docket.docketId, docket);
    }
  }
}

// Convert Map back to array
for (const [docketId, docket] of docketMap) {
  uniqueDockets.push(docket);
}

console.log(`Found ${uniqueDockets.length} unique open dockets`);

return uniqueDockets.map(docket => ({ json: docket }));

// Process all input items
