for (const item of $input.all()) {
  const webDocumentLink = item.json.webDocumentLink;
  const documentId = webDocumentLink.split('/document/')[1];

  // Try HTML first, but could fall back to PDF
  const contentUrl = `https://downloads.regulations.gov/${documentId}/content.html`;
  const pdfUrl = `https://downloads.regulations.gov/${documentId}/content.pdf`;

  results.push({
    json: {
      ...item.json,
      contentUrl: contentUrl,
      pdfUrl: pdfUrl,
      documentId: documentId
    }
  });
}

return results;

